import * as React from 'react';
import styled from 'styled-components';
import GameControls from './game-controls';
import Map from './map';
import { shuffleArray } from '../logic/utils';

const GameContainer = styled.div`
  display: flex;
  gap: 10px;
  padding: 10px;
  height: calc(100% - 20px);
`;

const CaptureModalBackdrop = styled.div`
  position: fixed;
  inset: 0;
  background: rgba(0, 0, 0, 0.6);
  display: flex;
  align-items: center;
  justify-content: center;
  z-index: 1000;
`;

const CaptureModalCard = styled.div`
  background: #ffffff;
  border-radius: 16px;
  padding: 24px 24px 72px;
  width: 95%;
  max-width: 1100px;
  height: 80vh;
  box-shadow: 0 24px 80px rgba(0, 0, 0, 0.5);
  display: flex;
  flex-direction: column;
  position: relative;
`;

const CaptureModalImagePlaceholder = styled.div`
  width: 100%;
  height: 80px;
  border-radius: 8px;
  background: #f2f2f2;
  display: flex;
  align-items: center;
  justify-content: center;
  color: #777;
  font-size: 14px;
  margin-top: 8px;
  margin-bottom: 16px;
`;

const CaptureModalTitle = styled.h2`
  margin: 0 0 8px;
  font-size: 20px;
`;

const CaptureModalText = styled.p`
  margin: 12px 0 20px;
  font-size: 14px;
  color: #444;
`;

const CaptureModalActions = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: flex-end;
  gap: 24px;
  position: absolute;
  left: 24px;
  right: 24px;
  bottom: 24px;
`;

const CaptureButton = styled.button`
  position: relative;
  width: 100px;
  height: 100px;
  border-radius: 12px;
  border: none;
  cursor: pointer;
  transition: transform 0.05s ease-in-out, box-shadow 0.05s ease-in-out,
    background-color 0.1s ease-in-out, filter 0.1s ease-in-out;
  display: flex;
  align-items: center;
  justify-content: center;

  &::after {
    content: attr(data-order);
    position: absolute;
    top: 6px;
    right: 6px;
    width: 20px;
    height: 20px;
    border-radius: 999px;
    background: rgba(0, 0, 0, 0.15);
    color: #111;
    font-size: 12px;
    display: flex;
    align-items: center;
    justify-content: center;
  }

  &:active {
    transform: translateY(1px);
    filter: brightness(0.9);
    box-shadow: 0 2px 6px rgba(0, 0, 0, 0.35);
  }

  &.cancel {
    background: #e5e5e5;
    
    &.active {
      background: #d4d4d4;
      box-shadow: inset 0 2px 4px rgba(0, 0, 0, 0.2);
    }
  }

  &.confirm {
    background: #16a34a;
    
    &.active {
      background: #15803d;
      box-shadow: inset 0 2px 4px rgba(0, 0, 0, 0.2);
    }
  }

  &.confirm-alt {
    background: #22c55e;
    
    &.active {
      background: #16a34a;
      box-shadow: inset 0 2px 4px rgba(0, 0, 0, 0.2);
    }
  }

  &.cancel-alt {
    background: #f97316;
    
    &.active {
      background: #ea580c;
      box-shadow: inset 0 2px 4px rgba(0, 0, 0, 0.2);
    }
  }
`;

const ButtonImage = styled.img`
  width: 60px;
  height: 60px;
  object-fit: contain;
`;

const CaptureModalColumn = styled.div`
  display: flex;
  flex-direction: column;
  gap: 12px;
`;

const shuffleTurnsOrder = (arr) => arr.concat(arr.splice(0, 1));

const availableColors = shuffleArray([
  '#7975b9',
  '#007F73',
  '#FB9AD1',
  '#FFC470',
  '#C5FF95',
  '#4793AF',
  '#ff7171',
]);

const Game = ({ map }) => {
  // ... остальной код компонента без изменений ...

  return (
    <GameContainer>
      {/* ... остальной JSX без изменений ... */}
      
      {pendingCaptureZone && (
        <CaptureModalBackdrop>
          <CaptureModalCard>
            <CaptureModalTitle>
              Захватить зону {pendingCaptureZone}?
            </CaptureModalTitle>
            <CaptureModalText>
              {captureState.isNeutral 
                ? 'Захват нейтральной территории. Выполните задание и выберите результат.' 
                : 'Захват вражеской территории. Здесь будет описание задания или подсказка для игрока. После выполнения задания выберите результат атаки для атакующих и защищающихся.'}
            </CaptureModalText>
            <CaptureModalImagePlaceholder>
              Image placeholder
            </CaptureModalImagePlaceholder>
            <CaptureModalActions>
              {captureState.isNeutral ? (
                <CaptureModalColumn style={{ margin: '0 auto' }}>
                  <CaptureButton
                    className={`confirm ${captureState.attackerAnswer === true ? 'active' : ''}`}
                    onClick={() => handleAttackerAnswer(true)}
                    data-order={captureState.attackerAnswer === true ? '1' : ''}
                  >
                    <ButtonImage src="/images/success.png" alt="Успешно" />
                  </CaptureButton>
                  <CaptureButton
                    className={`cancel ${captureState.attackerAnswer === false ? 'active' : ''}`}
                    onClick={() => handleAttackerAnswer(false)}
                    data-order={captureState.attackerAnswer === false ? '1' : ''}
                  >
                    <ButtonImage src="/images/fail.png" alt="Неуспешно" />
                  </CaptureButton>
                </CaptureModalColumn>
              ) : (
                <>
                  <CaptureModalColumn>
                    <CaptureButton
                      className={`confirm ${captureState.attackerAnswer === true ? 'active' : ''}`}
                      onClick={() => handleAttackerAnswer(true)}
                      data-order={
                        captureState.attackerAnswer === true 
                          ? (captureState.firstResponder === 'attacker' ? '1' : '2')
                          : ''
                      }
                    >
                      <ButtonImage src="/images/success.png" alt="Атакующие-верно" />
                    </CaptureButton>
                    <CaptureButton
                      className={`cancel ${captureState.attackerAnswer === false ? 'active' : ''}`}
                      onClick={() => handleAttackerAnswer(false)}
                      data-order={
                        captureState.attackerAnswer === false 
                          ? (captureState.firstResponder === 'attacker' ? '1' : '2')
                          : ''
                      }
                    >
                      <ButtonImage src="/images/fail.png" alt="Атакующие-неверно" />
                    </CaptureButton>
                  </CaptureModalColumn>
                  <CaptureModalColumn>
                    <CaptureButton
                      className={`confirm-alt ${captureState.defenderAnswer === true ? 'active' : ''}`}
                      onClick={() => handleDefenderAnswer(true)}
                      data-order={
                        captureState.defenderAnswer === true 
                          ? (captureState.firstResponder === 'defender' ? '1' : '2')
                          : ''
                      }
                    >
                      <ButtonImage src="/images/success.png" alt="Защищающиеся-верно" />
                    </CaptureButton>
                    <CaptureButton
                      className={`cancel-alt ${captureState.defenderAnswer === false ? 'active' : ''}`}
                      onClick={() => handleDefenderAnswer(false)}
                      data-order={
                        captureState.defenderAnswer === false 
                          ? (captureState.firstResponder === 'defender' ? '1' : '2')
                          : ''
                      }
                    >
                      <ButtonImage src="/images/fail.png" alt="Защищающиеся-неверно" />
                    </CaptureButton>
                  </CaptureModalColumn>
                </>
              )}
            </CaptureModalActions>
          </CaptureModalCard>
        </CaptureModalBackdrop>
      )}
    </GameContainer>
  );
};

export default Game;
