import React, { useState } from 'react';
import './App.css';

function App() {
  const [selectedOption, setSelectedOption] = useState(0);
  const [showPassword, setShowPassword] = useState(false);
  const [showActions, setShowActions] = useState(false);
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const CORRECT_PASSWORD = "1234"; // –ü–æ–º–µ–Ω—è–π—Ç–µ –ø–∞—Ä–æ–ª—å –ø—Ä–∏ –Ω–µ–æ–±—Ö–æ–¥–∏–º–æ—Å—Ç–∏

  const selectOption = (option) => {
    setSelectedOption(option);
    setShowPassword(true);
    setShowActions(false);
    setError('');
    setPassword('');
  };

  const checkPassword = () => {
    if (password === CORRECT_PASSWORD) {
      setError('');
      if (selectedOption === 1) {
        redirectToSite();
      } else {
        setShowPassword(false);
        setShowActions(true);
      }
    } else {
      setError('–ù–µ–≤–µ—Ä–Ω—ã–π –ø–∞—Ä–æ–ª—å! –ü–æ–ø—Ä–æ–±—É–π—Ç–µ —Å–Ω–æ–≤–∞.');
      setPassword('');
    }
  };

  const redirectToSite = () => {
    // –ó–∞–º–µ–Ω–∏—Ç–µ –Ω–∞ –Ω—É–∂–Ω—ã–π URL
    window.location.href = 'https://www.example.com';
  };

  const specialFunction = () => {
    alert('üéâ –°–ø–µ—Ü–∏–∞–ª—å–Ω–∞—è —Ñ—É–Ω–∫—Ü–∏—è –∞–∫—Ç–∏–≤–∏—Ä–æ–≤–∞–Ω–∞!');
    // –î–æ–±–∞–≤—å—Ç–µ —Å–≤–æ—é –ª–æ–≥–∏–∫—É –∑–¥–µ—Å—å
    console.log('–°–ø–µ—Ü–∏–∞–ª—å–Ω–∞—è —Ñ—É–Ω–∫—Ü–∏—è –≤—ã–ø–æ–ª–Ω–µ–Ω–∞!');
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      checkPassword();
    }
  };

  return (
    <div className="App">
      <div className="container">
        <h1>–í—ã–±–µ—Ä–∏—Ç–µ –æ–ø—Ü–∏—é –¥–æ—Å—Ç—É–ø–∞</h1>

        {/* –®–∞–≥ 1: –í—ã–±–æ—Ä –æ–ø—Ü–∏–∏ */}
        {!showPassword && !showActions && (
          <div className="button-grid">
            <button className="option-btn" onClick={() => selectOption(1)}>
              üîê –û–ø—Ü–∏—è 1 - –ü—Ä—è–º–æ–π –¥–æ—Å—Ç—É–ø
            </button>
            <button className="option-btn" onClick={() => selectOption(2)}>
              üóùÔ∏è –û–ø—Ü–∏—è 2 - –†–∞—Å—à–∏—Ä–µ–Ω–Ω—ã–π –¥–æ—Å—Ç—É–ø
            </button>
          </div>
        )}

        {/* –®–∞–≥ 2: –í–≤–æ–¥ –ø–∞—Ä–æ–ª—è */}
        {showPassword && (
          <div className="password-section">
            <h2>
              {selectedOption === 1 
                ? '–û–ø—Ü–∏—è 1 - –í–≤–µ–¥–∏—Ç–µ –ø–∞—Ä–æ–ª—å –¥–ª—è –ø—Ä—è–º–æ–≥–æ –¥–æ—Å—Ç—É–ø–∞' 
                : '–û–ø—Ü–∏—è 2 - –í–≤–µ–¥–∏—Ç–µ –ø–∞—Ä–æ–ª—å –¥–ª—è —Ä–∞—Å—à–∏—Ä–µ–Ω–Ω–æ–≥–æ –¥–æ—Å—Ç—É–ø–∞'
              }
            </h2>
            <input 
              type="password" 
              className="password-input" 
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              onKeyPress={handleKeyPress}
              placeholder="–í–≤–µ–¥–∏—Ç–µ –ø–∞—Ä–æ–ª—å..."
            />
            <button className="submit-btn" onClick={checkPassword}>
              –ü—Ä–æ–¥–æ–ª–∂–∏—Ç—å
            </button>
            {error && <div className="error-message">{error}</div>}
          </div>
        )}

        {/* –®–∞–≥ 3: –î–µ–π—Å—Ç–≤–∏—è –¥–ª—è –æ–ø—Ü–∏–∏ 2 */}
        {showActions && (
          <div className="action-buttons">
            <button className="action-btn redirect-btn" onClick={redirectToSite}>
              üåê –ü–µ—Ä–µ–π—Ç–∏ –Ω–∞ —Å–∞–π—Ç
            </button>
            <button className="action-btn function-btn" onClick={specialFunction}>
              ‚ö° –°–ø–µ—Ü–∏–∞–ª—å–Ω–∞—è —Ñ—É–Ω–∫—Ü–∏—è
            </button>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
